using ShopifySharp.Credentials;
using ShopifySharp.GraphQL;
using ShopifySharp.Infrastructure;
using ShopifySharp.Infrastructure.Serialization.Json;
using ShopifySharp.GraphQL.Generated.QueryBuilders.Operations;
using ShopifySharp.GraphQL.Generated.QueryBuilders.Types;

namespace ShopifySharp.GraphQL.Generated.QueryBuilders.Types
{
    public sealed class ProductUpdatePayloadQueryBuilder : FieldsQueryBuilderBase<ProductUpdatePayload, ProductUpdatePayloadQueryBuilder>
    {
        protected override ProductUpdatePayloadQueryBuilder Self => this;

        public ProductUpdatePayloadQueryBuilder() : this("productUpdatePayload")
        {
        }

        public ProductUpdatePayloadQueryBuilder(string name) : base(new Query<ProductUpdatePayload>(name))
        {
        }

        public ProductUpdatePayloadQueryBuilder(IQuery<ProductUpdatePayload> query) : base(query)
        {
        }

        public ProductUpdatePayloadQueryBuilder Product(Action<ShopifySharp.GraphQL.Generated.QueryBuilders.Types.ProductQueryBuilder> build)
        {
            var query = new Query<Product>("product");
            var queryBuilder = new ShopifySharp.GraphQL.Generated.QueryBuilders.Types.ProductQueryBuilder(query);
            build.Invoke(queryBuilder);
            base.InnerQuery.AddField<Product>(query);
            return this;
        }

        public ProductUpdatePayloadQueryBuilder UserErrors(Action<ShopifySharp.GraphQL.Generated.QueryBuilders.Types.UserErrorQueryBuilder> build)
        {
            var query = new Query<UserError>("userErrors");
            var queryBuilder = new ShopifySharp.GraphQL.Generated.QueryBuilders.Types.UserErrorQueryBuilder(query);
            build.Invoke(queryBuilder);
            base.InnerQuery.AddField<UserError>(query);
            return this;
        }
    }
}